INSERT INTO `game` VALUES (81, 1, 'JP0001-CUSA12615_00-ASIAPLACEHOLDER0', '《汤姆克兰西：全境封锁 2》数码标准版', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0001-CUSA12615_00-ASIAPLACEHOLDER0/1558084365000/image', '<b><font color=\"orange\">※另有包含此单独商品的套装包。请注意切勿重复购买。</font></b><br><br>历史终将铭记<br><br>在《汤姆克兰西：全境封锁 2》里，自由世界的命运岌岌可危。<br><br>带领一队菁英特工进入瘟疫后的华盛顿特区，恢复秩序，避免城市倾颓。<br><br>《汤姆克兰西：全境封锁 2》是一款在线动作射击 RPG 游戏，探索和玩家进展是最重要的要素。<br><br>• 解放华盛顿特区：在探索开放多变、充满敌意世界的同时，拯救濒临颓亡的国家。<br><br>• 和队伍一同茁壮：在在线多人游戏里和朋友并肩分工作战，或在敌对竞争的 PVP 模式互相对抗。<br><br>• 重新定义游戏终局：选择独特的特化职业，和朋友一起接受最艰巨的挑战。<br><br><b>Copyright：</b><br>© 2018 Ubisoft Entertainment. All Rights Reserved. Tom Clancy’s, The Division logo, the Soldier Icon, Ubisoft, and the Ubisoft logo are registered or unregistered trademarks of Ubisoft Entertainment in the US and/or other countries.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,日,英,韩', 1552611600000, '射击', 46000, 39560, 0, 46000, 39560, 0, 0, 46000, 0, -1, 443, 736, 0, 1556002096832, 1558119929624, '');
