INSERT INTO `game` VALUES (148, 1, 'UP5219-CUSA15110_00-ASIA000000000000', 'Ages of Mages: the Last Keeper', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP5219-CUSA15110_00-ASIA000000000000/1557252575000/image', '《Ages of Mages - The last keeper》是一款融合第三人称射击，魔法师元素的横版过关游戏！ 在唯美的魔幻世界里，四个蠢萌蠢萌的魔法师是如何打败死亡军团，救回的公主呢？官方简体中文支持！<br><br>你来得真是时候！王国正在被入侵，公主刚好被抓走！<br>4个蠢萌的魔法师能否承担“守护者”的责任，重振鼎盛的魔法师时代？<br>-----------------------------------------------------------------------------------<br>游戏特色<br>■ 畅快的3D横版射击动作游戏。唯美的魔幻世界。<br>■ 4种属性魔法师，各有特点。<br>■ 魔杖系统，20余种不同技能的魔杖等你挑选。<br>■ 丰富的技能树系统，逐步解锁强大魔法。<br>■ 包含Rogue-lite元素的随机迷宫关卡。<br>■ 7大章节，38个关卡，14场BOSS战。<br>■ 单人模式和本地多人合作。<br>■ 解锁多种有趣的成就。<br>■ 官方简体中文！<br><br><b>Copyright：</b><br>YFC TECHNOLOGY CO., LTD<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,英', 1550019600000, '射击', 9500, 8170, 0, 9500, 8170, 0, 0, 9500, 0, -1, 340, 5, 0, 1556002233038, 1558120057666, '');
