INSERT INTO `game` VALUES (308, 1, 'JP1091-CUSA13588_00-ASIA000000000000', 'Narcosis 深海惊悚', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP1091-CUSA13588_00-ASIA000000000000/1558084467000/image', '《Narcosis》是一款第一人称视角的恐怖冒险游戏。<br><br>正在太平洋深层采掘能源的潜水员遭遇突如其来的大地震，游戏就此揭开序幕。<br><br>玩家必须操作被流放在无情深海中的潜水员，靠着有限的光源与氧气浮出水面。<br><br>有时候会与同样赌上生死的海洋生物正面交锋。<br>有时候会见到遭遇不幸的伙伴惨状。<br><br>到底会先用尽氧气？还是先失去意识……？<br>在极限状态下所遭遇的光怪陆离，或许是潜水员精神崩坏的征兆吧。<br><br>在光线无法穿透而至的深海之下，用你的耳目和第六感奋力求生吧！<br><br><b>Copyright：</b><br>©2018 Honor Code, Inc. / Licensed and Published by 2018 CITY CONNECTION CO., LTD.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,日,英', 1540947600000, '冒险', 12500, 10750, 0, 12500, 10750, 0, 0, 12500, 0, -1, 150, 2, 0, 1556002620544, 1558120491755, '');
INSERT INTO `game` VALUES (309, 1, 'EP0001-CUSA05526_00-ASIAXGAMESPASS00', '《极限巅峰》- X Games 数码黄金版', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/EP0001-CUSA05526_00-ASIAXGAMESPASS00/1557254989000/image', '<b><font color=\"orange\">本产品内含《极限巅峰》主游戏，如果你已经拥有《极限巅峰》主游戏，请勿购买本产品。</font></b><br><br><b><font color=\"orange\">※在购买本套装包前若已购买单独商品。</font></b><br><br>在极致的《Steep》体验里踏上路程、大展身手！前往阿尔卑斯山和阿拉斯加的广阔开放世界驾驭 8 种极限运动，争夺传奇冬季运动竞赛「X Games」的金牌！<br><br>《Steep》X Games 黄金版包含<br>+《Steep》主游戏<br>+ 第 1 年 Season Pass<br>+ X Games Pass<br><br>玩家可以穿梭于雪地和蓝天之间自由探索山区开放世界、与好友一起滑雪并融合各种运动来创造狂野特技、尽情展现创意、将个人技能发挥到极限以完成疯狂特技，成为 X Games 的自由式滑雪名人，还能享受各种激发肾上腺素和趣味的额外内容，像是火箭飞鼠装、90 年代主题单双板滑雪模式和多样化的自订可能。<br><br><b>Copyright：</b><br>© 2018 Ubisoft Entertainment. All Rights Reserved. The Steep logo, Ubisoft, and the Ubisoft logo are trademarks of Ubisoft Entertainment in the US and/or other countries. Used under license from ESPN Enterprises, Inc. X Games is a trademark of ESPN, Inc<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,英', 1540947600000, '模拟', 28000, 24080, 0, 28000, 24080, 0, 0, 28000, 0, -1, 334, 346, 0, 1556002623271, 1558120493984, '');
INSERT INTO `game` VALUES (310, 1, 'HP1202-CUSA10898_00-XXXXXXXXXXXXXXXX', '强袭机甲', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP1202-CUSA10898_00-XXXXXXXXXXXXXXXX/1556269904000/image', '<font color=\"yellow\"><b>注意：本内容可使用PlayStation®Move动态控制器。</b></font><br>近未来的世界，人工智慧机器人逐渐取代军人与警察来维护世界和平。<br><br>但是，人工智慧防御网逐渐产生自我意识，它觉得世界最不安全的存在，就是人类。<br><br>因此，拥有思想的人工智慧向人类发动了战争。<br><br>为了拯救人类文明，只能依靠人类自己来对抗。<br><br>每个人都有义务保护我们的家园，我们的文化。以及我们的家人。<br><br>你的决定将影响我们的未来。<br><br>证明你有能力拯救这个世界。<br><br>加入强袭机甲特战队，代表人类与人工智慧奋战。<br><br>为使我们能保有原本的生活方式创造人类的未来。<br><br>超越自我，成为一位英雄，成为传说。<br><br>1 player<br>2GB minimum save size<br>DUALSHOCK®4<br>Online Play (Optional)<br>PlayStation®VR Required<br>PlayStation®Camera Required<br>Enhanced play with 1 PlayStation®Move动态控制器 <br>Enhanced play with 1 PlayStation®VR 射击控制器<br><br><b>Copyright：</b><br>USERJOY Technology Co., Ltd. Copyright©<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,日,英,韩', 1540861200000, '射击', 7800, 6708, 0, 7800, 6708, 0, 0, 7800, 0, -1, 367, 3, 0, 1556002625179, 1558120496182, '');
