INSERT INTO `game` VALUES (329, 1, 'EP0001-CUSA05264_00-ASIA000000000012', '《荣耀战魂》- 数码烈火行军版', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/EP0001-CUSA05264_00-ASIA000000000012/1557255015000/image', '<b><font color=\"orange\">※在购买本套装包前若已购买单独商品。</font></b><br><br>包含《荣耀战魂》主游戏，以及《烈火行军》扩展包。<br>参与战争的纷乱，选择你的阵营：骑士，维京人，武士或武林战士；游玩包含 PvP，PvE，合作模式或剧情战役等，刺激多元的游戏模式。<br><br>与史诗级战士决斗。使用来自四大阵营 16 种独特的自订英雄，任意选择你的战斗方式。<br><br>沉浸在剧情战役里，在全新的无限战场模式中提升英雄整体能力。<br><br>在七种多人模式里与朋友决斗，包含全新的 4 对 4 攻城战。<br><br>在全新的训练模式里学习战斗技巧，赢得奖赏，从战斗学徒成为大师。<br><br>全新的影像强化，让你身历其境，仿佛战斗成真。<br><br><b>Copyright：</b><br>© 2017 Ubisoft Entertainment. All Rights Reserved. The For Honor logo, Ubisoft, and the Ubisoft logo are trademarks of Ubisoft Entertainment in the US and/or other countries.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,英,韩', 1539738000000, '动作', 35000, 30100, 0, 35000, 30100, 0, 0, 35000, 0, -1, 370, 10, 0, 1556002664245, 1558120534145, 'EP0001-CUSA05264_00-ASIA000000000000');
