INSERT INTO `game` VALUES (352, 1, 'JP0571-CUSA13296_00-ASIA000000000000', 'ACA NEOGEO ZUPAPA！', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA13296_00-ASIA000000000000/1555060633000/image', '<b><font color=\"orange\">※本作是以MVS（大型电玩用NEOGEO）版游戏本篇为基础进行开发。内容可能会与家用NEOGEO或其他家用游戏主机上推出的版本有所不同。</font></b><br><br>「ZUPAPA！」是SNK于2001年推出的动作游戏。<br>玩家要操纵勇敢的年轻人「Zupapa」与「Zupipi」，救回被人带走的Zukko们！<br>可透过简单的操作，尽情奔驰在具有特色又欢乐的关卡中，并朝最终关卡前进。<br>本作也支持2人同时游玩。<br><br>「ACA NEOGEO」系列的开发宗旨，在于忠实地重现NEOGEO名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©SNK CORPORATION ALL RIGHTS RESERVED.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1538614800000, '动作', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 500, 2, 0, 1556002716423, 1558120579530, '');
