INSERT INTO `game` VALUES (378, 1, 'JP0571-CUSA13294_00-ASIA000000000000', 'ACA NEOGEO CYBER-LIP', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA13294_00-ASIA000000000000/1555060783000/image', '<b><font color=\"orange\">※本作是以MVS（大型电玩用NEOGEO）版游戏本篇为基础进行开发。内容可能会与家用NEOGEO或其他家用游戏主机上推出的版本有所不同。</font></b><br><br>「CYBER-LIP」是SNK于1990年推出的动作游戏。<br>凝聚了科学技术结晶的军用计算机「CYBER-LIP」突然失控了！<br>为阻止它以全人类为目标展开的杀戮行动，使用各式各样道具及武器与其对抗吧！<br><br>「ACA NEOGEO」系列的开发宗旨，在于忠实地重现NEOGEO名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©SNK CORPORATION ALL RIGHTS RESERVED.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1537405200000, '动作', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 425, 4, 0, 1556002833864, 1558120631339, '');
INSERT INTO `game` VALUES (379, 1, 'JP0102-CUSA11901_00-ASIACFULLGAME000', 'Capcom Belt Action Collection', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0102-CUSA11901_00-ASIACFULLGAME000/1558084268000/image', '<b><font color=\"orange\">本游戏部份场景演出包含闪光效果。游玩时请保持环境明亮。 <br>只有部分用户界面为中文，而游戏内容为海外版本，标题、语音、文字均为英语和日语。</font></b><br><br>再次感受游戏机中心的兴奋!<br>将Capcom的7款着名横向清版动作游戏集于1件作品内。<br>除了当然要有的代表作Final Fight外, Powered Gear及Battle Circuit 更是首次移植。<br><br>而当年在游戏机中心不能选择的难度和角色生命数等设定亦能更改。玩家还可根据个人喜好选择游玩日本版或海外版。其他内容还包括充满秘密插画的画廊!<br><br>超越世代的名作。<br>全通！一币通关！高得分！<br>传承下去！魂之Mega Crash！<br><br>收录游戏: Final Fighter / The King Of Dragons / Captain Commando / Knight of the Round / 吞食天地II 赤壁之战 / Powered Gear / Battle Circuit<br><br><b>Copyright：</b><br>©CAPCOM CO., LTD. 2018, ©CAPCOM U.S.A., INC. 2018 ALL RIGHTS RESERVED.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,日,英', 1537405200000, '动作', 15600, 13416, 0, 15600, 13416, 0, 0, 15600, 0, -1, 463, 110, 0, 1556002835605, 1558120633583, '');
