INSERT INTO `game` VALUES (392, 1, 'JP0571-CUSA13292_00-ASIA000000000000', 'ACA NEOGEO AGGRESSORS OF DARK KOMBAT', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA13292_00-ASIA000000000000/1555059841000/image', '<b><font color=\"orange\">※本作是以MVS（大型电玩用NEOGEO）版游戏本篇为基础进行开发。内容可能会与家用NEOGEO或其他家用游戏主机上推出的版本有所不同。</font></b><br><br>「AGGRESSORS OF DARK KOMBAT（痛快GANGAN行进曲）」是SNK于1994年推出的格斗游戏。<br>请由8位热血豪杰中选择1人，为了各自的目的而战并取得最终胜利。<br>有层次的画面、冲刺加上激烈攻击，让痛快战斗毫无限制！<br>要以技巧取胜还是靠力气压制！？敬请随心所欲地畅享大闹一番的乐趣吧。<br><br>「ACA NEOGEO」系列的开发宗旨，在于忠实地重现NEOGEO名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©SNK CORPORATION ALL RIGHTS RESERVED.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1536800400000, '格斗', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 467, 3, 0, 1556002864857, 1558120660240, '');
INSERT INTO `game` VALUES (393, 1, 'HP0700-CUSA10622_00-BLACKCLOVER11111', '黑色五叶草 骑士四重奏', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP0700-CUSA10622_00-BLACKCLOVER11111/1557251372000/image', '<b><font color=\"orange\">※另有包含此单独商品的套装包。请注意切勿重复购买。</font></b><br><br>◆PS4™「黑色五叶草 骑士四重奏」<br>【能以魔法对战动作游戏享受4对4团队战】，使用具备风、水、雷等各种属性又充满个性的魔法，进行4对4的团队战！角色可分为「斗士」、「治愈者」、「后援」、「射手」4种职位。必须掌握自己的职位和个人魔法的使用方式，团队合作进行战斗才能获得胜利。活用高地等地形，灵巧行动也是胜利关键。<br><br>【生活在壮阔魔法世界里，充满个性的角色们】<br>主角亚斯塔是在魔法代表一切的世界里，唯一「不会使用魔法」的少年。虽然他的境遇如此，他还是和相遇的伙伴们一起克服了种种困难，以被称为魔道士顶点的「魔法帝」为目标。例如将亚斯塔视为「劲敌」的天才魔道士尤诺，还有虽然是王族但无法顺利控制魔力的少女诺艾儿，由个性丰富的角色们交织出壮大的故事。<br><br>【原创故事中所描述的，「超越时空的战斗」】<br>在本游戏中，有现在播出中的动画「黑色五叶草」本篇未出现的原创故事模式可游玩。亚斯塔隶属的骑士团「黑色暴牛」，团长夜见介大突然返老还童了？同一时期，发生了谜样魔导士袭击三叶草王国的事件。亚斯塔和大家将面临种种难关。<br><br><b>Copyright：</b><br>©YUKI TABATA/SHUEISHA, TV TOKYO, BLACK CLOVER PROJECT     <br>Game ©2018 BANDAI NAMCO Entertainment Inc.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中', 1536800400000, '射击', 47900, 41194, 0, 47900, 41194, 0, 0, 47900, 0, -1, 300, 2, 0, 1556002866929, 1558120662166, '');
