INSERT INTO `game` VALUES (425, 1, 'JP0571-CUSA13291_00-ASIA000000000000', 'ACA NEOGEO FOOTBALL FRENZY', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA13291_00-ASIA000000000000/1555060377000/image', '<b><font color=\"orange\">※本作是以MVS（大型电玩用NEOGEO）版游戏本篇为基础进行开发。内容可能会与家用NEOGEO或其他家用游戏主机上推出的版本有所不同。</font></b><br><br>「FOOTBALL FRENZY」是SNK于1992年推出的运动游戏。<br>请从多采多姿的共10支队伍中选择1队，在联赛里不断获胜，设法赢得冠军吧。<br>多样化布阵踢法所带来的无数策略型式、超逼真的冲撞等要素，都让游戏更加火热。<br>还可以进行两人对战游玩。<br><br>「ACA NEOGEO」系列的开发宗旨，在于忠实地重现NEOGEO名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。<br>此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©SNK CORPORATION ALL RIGHTS RESERVED.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1535590800000, '运动', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 100, 1, 0, 1556002929115, 1558120720509, '');
INSERT INTO `game` VALUES (426, 1, 'JP0571-CUSA12478_00-ASIA000000000000', 'Arcade Archives ARGUS', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA12478_00-ASIA000000000000/1555060412000/image', '<b><font color=\"orange\">本作的游戏本篇仅收录日本版ROM。<br>游戏选项选单与说明书则支持日文、英文、法文、德文、意大利文、西班牙文。</font></b><br><br>「ARGUS」是JALECO于1986年推出的射击游戏。<br>玩家要一边善用自机上搭载的对空炮、对地炮以及提升能力道具，一边设法击破敌方浮游要塞Mega Argus。<br>随关卡而不同的装备，以及通过关卡时的降落场面，都让游戏更增添乐趣。<br><br>「Arcade Archives」系列的开发宗旨，在于忠实地重现大型机台名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©CITY CONNECTION CO., LTD. / ©2018 HAMSTER Co.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1535590800000, '射击', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 500, 2, 0, 1556002931067, 1558120722231, '');
