INSERT INTO `game` VALUES (453, 1, 'JP0571-CUSA10987_00-ASIA000000000000', 'ACA NEOGEO GAROU: MARK OF THE WOLVES', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA10987_00-ASIA000000000000/1555060447000/image', '<b><font color=\"orange\">※本作是以MVS（大型电玩用NEOGEO）版游戏本篇为基础进行开发。内容可能会与家用NEOGEO或其他家用游戏主机上推出的版本有所不同。</font></b><br><br>「GAROU: MARK OF THE WOLVES（饿狼传说: 狼之印记）」是SNK于1999年推出的对战格斗游戏。<br>由特瑞扶养长大的吉斯之子洛克担任主角，是『饿狼传说』系列的后续作品。<br>搭载Breaking等各种新系统，让对战变得更加深奥，做为一款杰作的呼声也相当高。<br><br>「ACA NEOGEO」系列的开发宗旨，在于忠实地重现NEOGEO名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。<br>此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©SNK CORPORATION ALL RIGHTS RESERVED.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1534381200000, '格斗', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 467, 3, 0, 1556002981715, 1558120774874, '');
