INSERT INTO `game` VALUES (494, 1, 'UP0006-CUSA10483_00-ASIA000000000003', '《Unravel Yarny》同捆包', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP0006-CUSA10483_00-ASIA000000000003/1557252447000/image', '<b><font color=\"orange\">※在购买本套装包前若已购买单独商品。</font></b><br><br>《Unravel Yarny》同捆包包含《Unravel》和《Unravel 2》。  <br>《Unravel》是一款视觉效果惊艳、基于物理原理的解谜平台游戏，介绍了一个由一根纱线制成的新颖、惹人喜爱的角色。随着他踏上看似非同凡响的旅途，围绕他自身的谜题也缓缓解开。<br>在《Unravel 2》中，你可以创造独一无二的 Yarny。 在本地合作模式中与其他 Yarny 建立关系，或进行单人模式，随着你们共同踏上旅途，友情也会在相互扶持中愈加深厚。<br><br><b>Copyright：</b><br>适用条件和限制。 欲了解详情，请参见www.ea.com/zh-cn/legal。<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1532480400000, '益智', 21000, 18060, 0, 21000, 18060, 0, 0, 21000, 0, -1, 500, 11, 0, 1556003071669, 1558120915650, 'UP0006-CUSA10483_00-ASIA000000000001');
