INSERT INTO `game` VALUES (554, 1, 'UP1003-CUSA00132_00-ASIAPLACEHOLDER4', 'The Elder Scrolls Online: Collection', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP1003-CUSA00132_00-ASIAPLACEHOLDER4/1557252607000/image', '<b><font color=\"orange\">※在购买本套装包前若已购买单独商品。</font></b><br><br>这款在线多人角色扮演游戏屡获殊荣，赶紧购入终极收藏版，一起加入上千万名玩家的行列。The Elder Scrolls Online Compendium 内含主游戏、Morrowind 篇章、Summerset 篇章，以及四个 DLC 游戏包。在历久弥新的《Elder Scrolls》世界体验无尽的冒险。<br><br>• 踏上冒险旅程：混沌主宰了泰姆瑞尔大陆。远古的敌人联合起来摧毁人间，国王的宝座虚位以待。为了拯救泰姆瑞尔而战，举起你的武器吧！<br>• 拯救晨风省：返回瓦登费尔岛，帮助半人半神重获失去的神力，阻止一颗流星坠毁在维威克城。<br>• 夏暮岛旅程：高等精灵的故乡史上首度向外地访客开放边境。前往夏暮岛，并揭开威胁世界的暗黑阴谋。<br>• 加赠 DLC 组合包：在 Imperial City、Orsinium、Thieves Guild 与 Dark Brotherhood DLC 尽享全新故事、技能、区域，以及更多精彩内容。<br><br><b>Copyright：</b><br>©2018 ZeniMax Media Inc. The Elder Scrolls® Online: Summerset™ developed by ZeniMax Online Studios LLC, a ZeniMax Media company. ZeniMax, The Elder Scrolls, Summerset, ESO Plus, Bethesda, Bethesda Softworks and related logos are registered trademarks or trademarks of ZeniMax Media Inc. in the US and/or other countries. All Rights Reserved.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1528160400000, '角色扮演游戏', 62900, 54094, 0, 62900, 54094, 0, 0, 62900, 0, -1, 456, 9, 0, 1556003198714, 1558121032967, 'UP1003-CUSA00132_00-ASIA000000000007');
INSERT INTO `game` VALUES (555, 1, 'UP1003-CUSA00132_00-ASIAPLACEHOLDER0', 'The Elder Scrolls Online: Summerset', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP1003-CUSA00132_00-ASIAPLACEHOLDER0/1557252617000/image', '<b><font color=\"orange\">※某些套装包会包含本产品。同时，本产品所包含的某些内容也能够单独购买。请勿重复购买。</font></b><br><br>加入 1000 多万名玩家的行列，同游历久弥新的 Elder Scrolls 世界，这款在线多人角色扮演游戏屡获殊荣，为玩家提供无限的冒险体验。参与泰姆瑞尔日益增加的故事情节，前往最新篇章中高等精灵令人惊艳的家乡，拯救世界免于毁灭，尽情畅玩 The Elder Scrolls Online 主游戏。<br><br>• 踏上冒险旅程 – 在第二纪元，混沌主宰了泰姆瑞尔大陆。远古的敌人联合起来摧毁人间，国王的宝座虚位以待。为了拯救泰姆瑞尔而战，举起你的武器吧！<br><br>• 夏暮岛旅程 – 高等精灵的故乡史上首度向外地访客开放边境。前往夏暮岛，并揭开威胁世界的暗黑阴谋。<br><br>• 多种玩法，供玩家体验 – 战斗、制造、偷窃或探索，并结合不同种类的配备和能力，打造你个人专属的游戏风格。<br><br><b>Copyright：</b><br>©2018 ZeniMax Media Inc. The Elder Scrolls® Online: Summerset™ developed by ZeniMax Online Studios LLC, a ZeniMax Media company. ZeniMax, The Elder Scrolls, Summerset, ESO Plus, Bethesda, Bethesda Softworks and related logos are registered trademarks or trademarks of ZeniMax Media Inc. in the US and/or other countries. All Rights Reserved.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1528160400000, '角色扮演游戏', 26000, 22360, 0, 26000, 22360, 0, 0, 26000, 0, -1, 475, 4, 0, 1556003200872, 1558121034642, 'UP1003-CUSA00132_00-ASIA000000000007');
