INSERT INTO `game` VALUES (711, 1, 'UP0006-CUSA06536_00-ASIA000000000023', '《EA SPORTS™ UFC® 3》豪华版', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP0006-CUSA06536_00-ASIA000000000023/1557478589000/image', '<b><font color=\"orange\">※在购买本套装包前若已购买单独商品。</font></b><br><br>《EA SPORTS™ UFC® 3》采用了名为“真实球员动作技术”（Real Player Motion Tech）的崭新动画技术，在体育游戏中提供最细致、最真实的运动员动作捕捉，为逼真格斗动作带来了革命性提升。 每一拳每一腿，每一次格挡反击，都经过尖端动作捕捉技术的捕捉和重建，以达到栩栩如生、拳拳到肉的动作和感受，带来更加紧张刺激的竞技格斗体验。 在G.O.A.T. 生涯模式中，玩家必须营造格斗热潮来吸引粉丝，赢取资金来在新的训练馆中训练，并且和其他斗士形成火药味十足的对头关系，以博得全世界的关注。 在您成为史上最佳的过程中，您在八角笼以外所做的宣传，和您在它里面的表现同样重要。 《EA SPORTS™ UFC® 3》豪华版包含传奇人物Bruce Lee，他将作为一名斗士出现在游戏的最轻量级、次轻量级、轻量级和次中量级分组中。 另外还包含3000 UFC Point，可帮助你在UFC Ultimate Team中打造终极格斗战队。<br><br><b>Copyright：</b><br> *有附带条件和限制。 请查看HTTPS://WWW.EA.COM/LEGAL了解详情。<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,英', 1518138000000, '运动', 42900, 36894, 0, 42900, 36894, 0, 0, 42900, 0, -1, 0, 0, 0, 1556003644027, 1558121360691, 'UP0006-CUSA06536_00-ASIAPLACEHOLDER0');
INSERT INTO `game` VALUES (712, 1, 'JP0571-CUSA10795_00-ASIA000000000000', 'ACA NEOGEO BLAZING STAR', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA10795_00-ASIA000000000000/1555059950000/image', '<b><font color=\"orange\">※本作是以MVS（大型电玩用NEOGEO）版游戏本篇为基础进行开发。内容可能会与家用NEOGEO或其他家用游戏主机上推出的版本有所不同。</font></b><br><br>「BLAZING STAR」是SNK于1998年推出的射击游戏。<br>玩家可从6种不同的战斗机中选择一架，藉此对抗制造出生物兵器的元凶『邪恶兵器』。<br>本作的一大特色是蓄积能量打倒敌人就能赚取高分，是款能够挑战积分纪录的趣味作品！<br><br>「ACA NEOGEO」系列的开发宗旨，在于忠实地重现NEOGEO名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。<br>此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©SNK CORPORATION ALL RIGHTS RESERVED.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1518051600000, '射击', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 467, 6, 0, 1556003646092, 1558121362337, '');
