INSERT INTO `game` VALUES (735, 1, 'EP1302-CUSA06631_00-ASIASTRIKERS0000', 'Strikers Edge', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/EP1302-CUSA06631_00-ASIASTRIKERS0000/1557254785000/image', '《Strikers Edge》是一款古代战士们展开的类似中世躲避球游戏。通过在线与本地模式向对手挑战一下。成就伟大，成为顶级striker!<br><br>战斗躲避球：《Strikers Edge》是一款类似战斗躲避球游戏！无论使用什么手段，玩家要打败敌人！<br>要提高实力：要提高命中率，要预测对手的攻击，要获胜心理战打败敌人！<br>在线与本地多人：通过在线或本地多人模式，在8个独特的striker与4个不同的竞技场种选择对战。与朋友一起配合2对2的比赛，或在1对1的对战上成为最顶级的striker！<br>选择与熟练：每个striker都有独一无二的特技、武器、能力与风格。您想熟练哪一个striker？<br><br><b>Copyright：</b><br>© Plug In Digital. All rights reserved.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,英,韩', 1517360400000, '街机', 11100, 9546, 0, 11100, 9546, 0, 0, 11100, 0, -1, 378, 9, 0, 1556003692321, 1558121422640, '');
INSERT INTO `game` VALUES (736, 1, 'KP0004-CUSA09882_00-GAME000000000000', '新次元游戏 战机少女 VIIR', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/KP0004-CUSA09882_00-GAME000000000000/1558084206000/image', '<b><font color=\"orange\">《战机少女》系列最新作将于PlayStation®4登场，并首度加入对应 PlayStation®VR 虚拟实境内容！</font></b><br><br>回忆与日常以及你所交织而成的故事<br><br>「游戏业界」中存在着各种各样的次元，其中有个濒临崩坏的「零次元」世界。<br><br>零次元最后的守护女神「天王星涡芽」<br>与将世界导向灭亡的巨躯暴神「黝暗女神」开始了孤军奋战。<br><br>她的悲伤情感会召唤出超次元世界的普剌尼禔努守护女神「妮普禔努」。<br>以两人的相遇为契机，将遇到更多其他的伙伴。<br><br>――身为玩家的你, 也将可以一同……――<br><br>拯救世界后回归日常的女神, 降临到了你的房间。<br>她们的日常……还有对\"你\"的心意……<br><br><b>Copyright：</b><br>©2018 IDEA FACTORY / COMPILE HEART All rights reserved. Licensed to and published by CFK.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,韩', 1517274000000, '角色扮演游戏', 41800, 35948, 0, 41800, 35948, 0, 0, 41800, 0, -1, 478, 54, 0, 1556003695030, 1558121424875, '');
