INSERT INTO `game` VALUES (765, 1, 'HP2005-CUSA10260_00-BLEEDASIA0000000', 'Bleed', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP2005-CUSA10260_00-BLEEDASIA0000000/1557252505000/image', '女孩Wryn有着强大的火力和伟大的梦想──她想成为终极电玩游戏英雄！在她夺下此地位的刺激精彩过程中，帮助她打倒史上最伟大的英雄们，让全世界看看真正的英雄是什么样子！<br><br>『Bleed』是一款动作平台游戏，主打潇洒帅气、有如特技的闪躲动作，以及流畅、富挑战性的游玩方式。没有无聊桥段──只有你凭着自身技巧挑战七个关卡，关卡中充满敌人、障碍物、致命的机关，以及别出心裁的头目！<br><br>游戏特色：<br><br>・ 独特的操控配置，可让玩家施展疯狂的高速动作<br>・ 7个充满多重机关与头目的关卡<br>・ 4种难易度等级，每种等级都会重新编排敌人配置与头目模式<br>・所有模式均支持本地端2人协力游玩（需要至少一个控制器）<br>・3名可解锁的角色，各自有不同的游玩方式<br>・在Arcade Mode（街机模式）仅凭着一条命撑完整个游戏<br>・在Challenge Mode（挑战模式）挑选至多三名头目与你对战<br><br><b>Copyright：</b><br>© Bootdisk Revolution, Digerati Distribution and Marketing LLC. Licensed to and published by Eastasiasoft Limited. All Rights Reserved.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1515632400000, '动作', 10200, 8772, 0, 10200, 8772, 0, 0, 10200, 0, -1, 500, 4, 0, 1556003815622, 1558121554841, '');
INSERT INTO `game` VALUES (766, 1, 'HP0082-CUSA09574_00-ASIAPLACEHOLDER0', 'DISSIDIA FINAL FANTASY NT', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP0082-CUSA09574_00-ASIAPLACEHOLDER0/1557367115000/image', '<b><font color=\"orange\">※另有包含此单独商品的套装包。请注意切勿重复购买。</font></b><br><br>【游戏内容说明】<br>历代FINAL FANTASY系列作品的战士们再度集结！<br><br>本作品为重视与战友互相合作的3对3团队对战型动作游戏。<br>采用BRAVE系统，并与25名以上各具特色的角色们交织出具有无限战略性的战斗。<br>善用「HEAVY」、「SPEED」、「SHOOT」、「UNIQUE」以上4种不同的特性，引导队伍获得胜利吧。<br>本作不仅能畅享一般战斗，更加入了3人协力挑战的全新战斗规则。<br>同时还收录了剧情影片，内容将描写初次登场的众神与战士们的『序曲』。<br><br>※于支持在线多人游玩的环境下，欲进行在线多人游玩，需加入PlayStation®Plus会籍（此为付费服务）。<br><br><b>Copyright：</b><br>©2018 KOEI TECMO GAMES/SQUARE ENIX CO., LTD. All Rights Reserved.　CHARACTER DESIGN: TETSUYA NOMURA　ILLUSTRATION:©2017 YOSHITAKA AMANO<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,韩', 1515632400000, '角色扮演游戏', 23800, 20468, 0, 23800, 20468, 0, 0, 23800, 0, -1, 371, 154, 0, 1556003818080, 1558121557987, '');
