INSERT INTO `game` VALUES (772, 1, 'EP4541-CUSA04667_00-ASIA000000000000', 'Reus', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/EP4541-CUSA04667_00-ASIA000000000000/1556269697000/image', '<b><font color=\"orange\">支持语言包括英文与简体中文。</font></b><br><br><b><font color=\"orange\">※另有包含此单独商品的套装包。请注意切勿重复购买。</font></b><br><br>你可以在『Reus』中操控强大的巨人随心所欲塑造这颗星球，创造山脉、海洋、森林等等，再以动植物和矿物充实你的星球。但是唯有一样事物超出你的掌握：善恶兼具的「人类」。你可以塑造他们的世界，却无法形塑他们的意志。提供资源，他们可以成长茁壮；但给了太多，却可能使其贪婪。<br><br>主要特色：<br>-操控4名各具独特能力的强大巨人<br>-按照个人想法塑造星球，尝试不同种类的地形<br>-融合升级与协同机制的复杂系统，提供多变的玩法<br>-观察人类，支使巨人给予奖赏或惩戒<br>-享受充满趣味的艺术风格与精彩的配乐<br>-超过100种动植物与矿物，丰富你的星球<br>-帮助人类成就众多发展，解锁新的内容<br><br><b>Copyright：</b><br>Published by SOEDESCO, Koddeweg 13, 3194 DH, Hoogvliet. © 2013-2016, Abbey Games B.V. FMOD Studio, copyright © Firelight Technologies Pty, Ltd., 2012-2016.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,英', 1515632400000, '战略', 13700, 11782, 0, 13700, 11782, 0, 0, 13700, 0, -1, 250, 4, 0, 1556003830475, 1558121570159, '');
