INSERT INTO `game` VALUES (841, 1, 'EP4008-CUSA07582_00-ASIA000000000000', 'Outcast - Second Contact', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/EP4008-CUSA07582_00-ASIA000000000000/1555048178000/image', 'Outcast – Second Contact 大举重制经典邪典游戏 Outcast，是电玩史上第一款 3D 开放世界游戏，更是当代动作冒险游戏的先驱。<br><br>扮演 Cutter Slade 这个通过无限试炼的正宗菁英战士，到一个外星球进行一场背水一战的任务。探索这个充满奇幻与科学的浩瀚世界，探索异星城市、对抗凶猛敌人，并发掘超先进文明世界的秘密。在这个系统化的动态世界做出正确抉择，这两个宇宙的命运就端看你的成败了。<br><br>- 类科幻电影冒险，游戏时间超过 50个小时<br>- 可以使用各式武器，打造华丽战斗场面<br>- 广大的动态开放世界给你完全的游戏自由度<br><br><b>Copyright：</b><br>©2017  Published by Bigben Interactive and developed by Appeal.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1510880400000, '冒险', 29900, 25714, 0, 29900, 25714, 0, 0, 29900, 0, -1, 475, 4, 0, 1556004092952, 1558121732887, '');
