INSERT INTO `game` VALUES (872, 1, 'JP0571-CUSA09696_00-ASIA000000000000', 'ACA NEOGEO MUTATION NATION', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA09696_00-ASIA000000000000/1555060419000/image', '「MUTATION NATION」是SNK于1992年推出的动作游戏。<br>为了拯救遭到天才生化学家变成突变体的人们以及逐渐荒废的故乡，两名年轻人决定挺身而出。<br>请施展惊心动魄的连续技与6大必杀技，奋勇作战吧。<br><br>「ACA NEOGEO」系列的开发宗旨，在于忠实地重现NEOGEO名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。<br>此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©SNK CORPORATION ALL RIGHTS RESERVED.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1508979600000, '动作', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 417, 6, 0, 1556004168775, 1558121812892, '');
INSERT INTO `game` VALUES (873, 1, 'JP0571-CUSA03710_00-ASIA000000000000', 'Arcade Archives ELEVATOR ACTION', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA03710_00-ASIA000000000000/1555059945000/image', '「ELEVATOR ACTION」是TAITO于1983年推出的动作游戏。<br>玩家的目的是以间谍身分只身潜入大楼，窃取所有机密文件并离开此处。<br>可操作电梯压扁敌人，或是藉由电灯来打倒敌人，畅享多种动作乐趣。<br><br>「Arcade Archives」系列的开发宗旨，在于忠实地重现大型机台名作游戏。<br>也能够变更游戏难度等各种游戏设定，或者重现出当年映像管电视的画面气氛。<br>此外，还可以藉由在线排行榜和全世界的玩家较量分数。<br>请务必要来畅玩这款筑起游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>© TAITO CORPORATION 1983 ALL RIGHTS RESERVED.<br>Arcade Archives Series Produced by HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1508979600000, '动作', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 500, 3, 0, 1556004170186, 1558121815558, '');
