INSERT INTO `game` VALUES (948, 1, 'HP9000-CUSA08095_00-ASIAPLACEHOLDER0', 'KNACK 2', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP9000-CUSA08095_00-ASIAPLACEHOLDER0/1558084253000/image', '来看看钠克，这位身怀绝技的好夥伴可自由转换身型大小并再度归来！请尽情享受这款出自PlayStation®游戏开发者所带来的友情冒险。<br><br>钠克的奇异能力<br>• 超过20种技能动作，包括回力镖，鞭技，刃剑，复合炸弹以及抱摔。<br>• 可自由控制身型大到与建筑物一般高，或缩小到仅有60公分的迷你尺寸。<br>• 跃入坦克或机器人进行操作或摧毁。<br>• 收集神器可强化并可于敌群中冲锋陷阵。<br>适合阖家同乐 <br>• 顺畅的协力模式，可让玩家随时加入游戏同乐<br>• 小朋友易于上手，大人们易于挑战的游戏设计<br>• 随手就玩，或坐下来场钠克马拉松<br>冒险与解谜要素<br>• 从欲使古代兵器及巨型泰坦复活的恶党手里拯救世界 <br>•令人惊豔的图像配上生动色彩<br>• 各种因应益智解谜要素的技能等级<br><br><b>Copyright：</b><br>©2017 Sony Interactive Entertainment Inc.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,英,韩', 1504569600000, '动作', 26800, 23048, 0, 26800, 23048, 0, 0, 26800, 0, -1, 444, 192, 0, 1556004330680, 1558122000342, '');
INSERT INTO `game` VALUES (949, 1, 'UP0082-CUSA08479_00-ASIA000000000002', 'Life is Strange: Before the Storm Complete Season', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP0082-CUSA08479_00-ASIA000000000002/1558083965000/image', '<b><font color=\"orange\">※某些套装包会包含本产品。同时，本产品所包含的某些内容也能够单独购买。请勿重复购买。</font></b><br><br>省钱看这里！只要购买『Life is Strange: Before the Storm』Complete Season（Episodes 1-3），各章节发布后，即可自动收到最新内容。<br><br>『Life is Strange: Before the Storm』的主角是16岁的叛逆少女Chloe Price，她和美丽又受欢迎的天之骄女Rachel Amber缔结了一段出乎意料的友谊。当Rachel的世界因为家族秘密而天翻地覆时，是她们新建立的情谊给了彼此战胜心魔的力量。<br><br>游戏特色<br>•受选择与结果影响走向的叙事冒险<br>•你的选择将导出不同结局<br>•「Backtalk（暂译：顶嘴）」－风险／奖励交互方式，供Chloe以伶牙俐齿挑衅或说服听者<br>•在游戏世界中以趣味标签和图画留下你的标记<br>•选择Chloe的服装，看看他人有什么反应<br>•由英国独立民谣乐团Daughter创作的独特授权独立音乐与原创配乐<br><br><b>Copyright：</b><br>Life is Strange: Before The Storm © 2017 Square Enix Ltd. All Rights Reserved. LIFE IS STRANGE and LIFE IS STRANGE: BEFORE THE STORM are registered trademarks or trademarks of Square Enix Ltd.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1504227600000, '冒险', 10700, 9202, 0, 10700, 9202, 0, 0, 10700, 0, -1, 422, 23, 0, 1556004332999, 1558122002058, 'UP0082-CUSA08479_00-ASIA000000000000');
