INSERT INTO `game` VALUES (1005, 1, 'HP9000-CUSA07184_00-UCUS987110000001', 'Patapon™ Remastered', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP9000-CUSA07184_00-UCUS987110000001/1558084495000/image', '<b><font color=\"orange\">*本游戏仅支援繁体中文。</font></b><br><br>Patapon™完全活用PS4™和PS4™Pro的效能，重回PlayStation®怀抱！ <br><br>Patapon™是一款2D平台动作的节奏游戏，经过重制后对应1080p和4K分辨率的电视，再次登陆PlayStation®4 。由于受到基哥咚恶势力的逼迫，多年来Patapon 一族只能居住在边陲地带。在Patapon可爱的外表下隐藏着一颗只有他们神明的太鼓声才能驱使的战斗之心。他们等待着「全能的神明」回来引领他们对抗敌人，带他们重返家园。太鼓声再度响起，他们漫长的等待终于结束了。<br><br><b>Copyright：</b><br>©2008,2017 Sony Interactive Entertainment Inc. ©2008,2017 Rolito/Interlink.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中', 1501549200000, 'MUSIC/RHYTHM', 10800, 9288, 0, 10800, 9288, 0, 0, 10800, 0, -1, 358, 375, 0, 1556004450300, 1558122260484, '');
