INSERT INTO `game` VALUES (1110, 1, 'HP0700-CUSA05955_00-ASIAPLACEHOLDER0', 'Little Nightmares', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP0700-CUSA05955_00-ASIAPLACEHOLDER0/1555660503000/image', '<b><font color=\"orange\">※另有包含此单独商品的套装包。请注意切勿重复购买。</font></b><br><br>置身于黑暗风格的异想故事──Little Nightmares的世界中，面对属于你的儿时梦魇！贪颚号──是艘巨大又神秘的船只，盘踞其中的腐败灵魂正打算饱餐一顿，快帮助小六从中脱逃吧。<br>在慢慢探索后就会发觉，这令人胆颤心惊的未知环境，不仅只有要逃离的监狱，还有许多可以挖掘秘密的场所。<br>重新找回你的童心，解放想象力，找寻出口吧！<br><br><b>Copyright：</b><br>Little Nightmares™ & ©BANDAI NAMCO Entertainment Europe<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,韩', 1493341200000, '益智', 20900, 17974, 0, 20900, 17974, 0, 0, 20900, 0, -1, 463, 268, 0, 1556004790961, 1558122629901, '');
INSERT INTO `game` VALUES (1111, 1, 'JP0571-CUSA07498_00-ASIARDC000000001', 'ACA NEOGEO THE KING OF FIGHTERS \'95', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA07498_00-ASIARDC000000001/1555054080000/image', '「THE KING OF FIGHTERS \'95」是SNK于1995年推出的格斗游戏。<br>这是前作「\'94」的续集作品，除了「饿狼传说」、「龙虎之拳」等热门作品的角色之外，还有八神庵以主角草薙京的竞争对手之姿参战。<br>透过内建队伍编辑机能，玩家能够用自己喜欢的角色来组队了。<br><br>「ACA NEOGEO」系列的开发宗旨，在于忠实地重现NEOGEO名作游戏。<br>还可以藉由在线排行榜和全世界的玩家较量分数。<br><br><b>Copyright：</b><br>©SNK CORPORATION ALL RIGHTS RESERVED.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日,英', 1493254800000, '格斗', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 408, 26, 0, 1556004792992, 1558122631561, '');
