INSERT INTO `game` VALUES (1231, 1, 'HP0507-CUSA05880_00-FATEEXTELLA00000', 'Fate/EXTELLA', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP0507-CUSA05880_00-FATEEXTELLA00000/1557478520000/image', '◆Fate全新动作游戏登场<br>一段描绘前所未见「新世界（EXTELLA）」，由奈須きのこ（奈须蘑菇）全新执笔撰写之『圣杯战争后』的故事如今在此揭开序幕。<br>「Fate」系列众多角色将跨越作品藩篱于本作梦幻邂逅。英灵们将纵横奔驰于战场之上，使用宝具等华丽技能展开战斗。<br><br><b>Copyright：</b><br>©TYPE-MOON ©2016 Marvelous Inc.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,韩', 1481763600000, '动作', 46800, 40248, 0, 46800, 40248, 0, 0, 46800, 0, -1, 454, 325, 0, 1556005112447, 1558122877745, '');
