INSERT INTO `game` VALUES (1440, 1, 'UP1309-CUSA05108_00-ASIA000000000000', 'Steredenn', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP1309-CUSA05108_00-ASIA000000000000/1557252648000/image', 'Steredenn是一款激烈的、不规则的射击类游戏，嵌入大幅漂亮的像素，拥有疯狂的终极战斗。登上您的战舰并加入到永不停止对抗可怕的空间海盗的生存之战中。<br><br>Steredenn现在处于外星战役和类似盗贼的聚集地。享受游戏的完美设置、激烈的终极战斗、（近似）随机生成的环境、奇怪的宇宙活动和惊人的大型军火库所带来的无限游戏可玩性。利用很多的升级在您的战斗中增强您的飞船并在宇宙中建立您自己的路径。<br><br>- 漂亮的美工设计和宇宙飞船。<br>- 激烈的终极战斗。<br>- 使人入迷，残酷无情。<br>- 无限的街机游戏设置。<br>- 快速且紧凑的赛事。<br>- 无限的可玩性：级别（几乎是）随机生成的。<br>- 武器库：35种武器和25种互动和组合的升级。<br>- 评分：打败您的朋友并成为最优秀的！<br><br><b>Copyright：</b><br>© Pixelnest Studio<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,日,英,韩', 1467162000000, '射击', 10100, 8686, 0, 10100, 8686, 0, 0, 10100, 0, -1, 367, 21, 0, 1556005680851, 1558123387999, '');
