INSERT INTO `game` VALUES (1453, 1, 'HP0117-CUSA05530_00-PKG000000000GAME', '√Letter-方根书简-', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/HP0117-CUSA05530_00-PKG000000000GAME/1558084543000/image', '「我杀了人。犯下的罪必须偿还。<br>　必须就此告别了。再见。」<br><br>这是一款为了确认高中时的笔友文野亚弥在最后一封信中所写内容的真相，必须凭借着当年<br>与她来往的十封信，探索有着美丽的自然与历史人文背景的岛根县境内城市，设法查明谜团<br>真相的推理冒险游戏。<br><br>玩家将得在反复进行回想十五年前信件内容的「信件部分」、寻访岛根各地找出知道真相的<br>「七位同学」的「探索部分」，以及在找出同学后与其当面对质问出真相的「追问部分」，<br>借此一点一滴追查出这位笔友的真相。<br><br><b>Copyright：</b><br>©2016 KADOKAWA GAMES<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中', 1466035200000, '冒险', 33800, 29068, 0, 33800, 29068, 0, 0, 33800, 0, -1, 451, 1715, 0, 1556005768715, 1558123419192, '');
