INSERT INTO `game` VALUES (1464, 1, 'UP4252-CUSA04448_00-ASIA000000000000', 'Crazy Strike Bowling EX', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP4252-CUSA04448_00-ASIA000000000000/1555046601000/image', '<font color=\"yellow\"><b>注意：本内容可使用PlayStation®Camera游玩。</b></font><br>快加入Aiden、Jenkins先生和一大票可爱又趣味满点的角色，游玩『Crazy Strike Bowling EX』这款适合全家同乐的疯狂保龄球游戏！从充满异国情调的亚马逊丛林到埃及金字塔，在遥远的新保龄球场上击出「全倒」吧！和好友在示范赛模式或战斗模式中互相较量，或者在挑战模式闪躲障碍！疯狂畅玩，运用角色的特殊招式扫光保龄球瓶，同时解锁秘密的世界和服装！具备升级系统，『Crazy Strike Bowling EX』将一球击中各年龄层的心！<br><br><b>Copyright：</b><br>© 2015 Corecell Technology Co., Ltd.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1464742800000, '运动', 8700, 7482, 0, 8700, 7482, 0, 0, 8700, 0, -1, 300, 2, 0, 1556005791607, 1558123444461, '');
INSERT INTO `game` VALUES (1465, 1, 'JP0571-CUSA04469_00-ASIAHAMPRDC00001', 'Arcade Archives Armed F', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA04469_00-ASIAHAMPRDC00001/1555055783000/image', '「Armed F」是日本物产于1988年推出的射击游戏。<br>要装上可采取多彩多姿攻击方式的装甲，在昆虫关卡与机械关卡等各式各样世界中战斗。<br>将装甲翻转进行阵型变换，用广范围攻击来歼灭敌人吧。<br><br>「Arcade Archives」在忠实地重现大型机台名作游戏的同时，也支持PS4™专属的机能。<br>也可以使用SHARE机能来分享游玩画面或影片，以及藉由在线排行榜和其他玩者较量分数。<br>请务必要来畅玩这款筑起电玩游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©2016 HAMSTER Co.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日', 1464310800000, '射击', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 420, 5, 0, 1556005794067, 1558123446238, '');
