INSERT INTO `game` VALUES (1722, 1, 'UP0006-NPUB50310_00-ASIA000000000000', '宝开游戏包', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP0006-NPUB50310_00-ASIA000000000000/1554785711000/image', '<b><font color=\"orange\">※在购买本追加内容前若已购买个别包。</font></b><br><br>游戏包中包含《植物大战僵尸：花园战争》以及在该游戏中使用的 20 万枚金币；此外还包含 Peggle 2 Magical Masters Edition。《植物大战僵尸：花园战争》 的战火如火如荼，玩家可选择全新火爆的合作模式，也可在多玩家模式中获得激烈的战斗体验。在这地雷遍布的世界，玩家既能体验传统在线射击游戏的深度，也可重温《植物大战僵尸》 让人会心一笑的幽默，在这里，让僵尸和植物灰飞烟灭吧。体验终极版的 Peggle 2，现推出魔法大师版本！完成迄今为止推出的全新大师、服装、关卡、挑战等所有添加内容，此外，这个版本提供了众多的金钱和彩虹！<br><br><b>Copyright：</b><br>SEE FINAL PRODUCT DETAILS, EULA AND CONTENT PROTECTION TECHNOLOGY ATWWW.EA.COM/1/PRODUCT-EULAS IF AND WHEN AVAILABLE. INTERNET CONNECTION, ORIGIN ACCOUNT, REGISTRATION WITH SERIAL CODE AND ACCEPTANCE OF END USER LICENSE AGREEMENT MAY BE REQUIRED TO PLAY. EA ONLINE PRIVACY AND COOKIE POLICY AND TERMS OF SERVICE CAN BE FOUND AT WWW.EA.COM. YOU MUST BE 13+ TO REGISTER FOR AN ORIGIN ACCOUNT. EA MAY DISCONTINUE ONLINE FEATURES AFTER 30 DAYS NOTICE AT WWW.EA.COM/1/SERVICE.UPDATES.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1434675600000, '射击', 21000, 18060, 0, 21000, 18060, 0, 0, 21000, 0, -1, 0, 0, 0, 1556006468500, 1558124139870, 'UP0006-CUSA00453_00-ASIAPLANTSZOMBIE');
