INSERT INTO `game` VALUES (1734, 1, 'JP0571-CUSA01543_00-ASIA000000000000', 'Arcade Archives Mr.GOEMON', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0571-CUSA01543_00-ASIA000000000000/1555304246000/image', '「Mr.GOEMON」是1986年推出的动作游戏。<br>请避免被敌人捉住，一边运用各式各样道具一边朝终点前进吧。<br>本作成为了当年被称为KONAMI招牌作品的「大盗五右卫门系列」之基础，是一款名作。<br><br>「Arcade Archives」在忠实地重现大型机台名作游戏的同时，也支持PS4™专属的机能。<br>也可以使用SHARE机能来分享游玩画面或影片，以及藉由在线排行榜和其他玩者较量分数。<br>请务必要来畅玩这款筑起电玩游戏辉煌年代的名作。<br><br><b>Copyright：</b><br>©Konami Digital Entertainment<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日', 1432774800000, '街机', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 433, 3, 0, 1556006556687, 1558124224553, '');
