INSERT INTO `game` VALUES (1791, 1, 'UP0082-CUSA00994_00-ASIA000000000000', 'FINAL FANTASY TYPE-0 HD', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP0082-CUSA00994_00-ASIA000000000000/1558084031000/image', 'FINAL FANTASY TYPE-0 HD有着代入感极高的世界观和多名令人印象深刻的人物，并且保持着本系列一如既往的制作水准，将带给玩家不负最终幻想之名的高品质游戏和故事。<br>本作无疑将会大受欢迎。故事的舞台背景是生气蓬勃且引人入胜的名为奥利恩斯的世界。在那里，国家间的纷争正逐渐演变成分割大陆的战争…<br>相互对立的国家之一“朱雀”，有着培养精英候补生的魔导院。“零班”是聚集了魔导院中精英中之精英的班级。成为“零班”的成员，加入到局势紧张的奥利恩斯吧！<br>在令人震惊的全新战斗系统下，凭借强力的魔法和战斗能力，背负着残酷宿命去打倒帝国军吧！届时，隐藏在战火背后的真相将被揭露。<br><br><b>Copyright：</b><br>© 2011,2015 SQUARE ENIX CO., LTD. All Rights Reserved. CHARACTER DESIGN：TETSUYA NOMURA<br><br>单次购买後，本内容可於被购买账号指定为「常用PS4™」的PS4™主机和另一台以购买账号登入的PS4™主机上游玩。<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1426640400000, '动作', 19800, 17028, 0, 19800, 17028, 0, 0, 19800, 0, -1, 460, 89, 0, 1556006819323, 1558124399543, '');
INSERT INTO `game` VALUES (1792, 1, 'UP4382-CUSA00570_00-ASIA000000000000', 'Tiny Troopers Joint Ops', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP4382-CUSA00570_00-ASIA000000000000/1555553313000/image', '<font color=\"orange\"><b>※此商品除PlayStation®4用内容外，亦包含适用于PlayStation®3, PlayStation®Vita之内容。关于PlayStation®3, PlayStation®Vita内容的详细信息，请于PlayStation®Store确认。</b></font><br><br>『Tiny Troopers Joint Ops』是一款小巧的史诗大型电玩射击游戏。游戏的迷你地图充满着动作元素和爆破场面！精通各种武器和特殊士兵，并且操控迷你尖兵和世界各地的邪恶敌人对战。进行任务时，收集奖章、军籍牌和情报，巩固你的胜利吧！游戏提供七小时以上的枪林弹雨爆炸体验，『Tiny Troopers Joint Ops』的人物外型虽小，但是战争规模庞大！<br><br>购买后将获授权下载PS4™,PS3™和PS Vita版本。<br><br><b>Copyright：</b><br>© 2014 Kukouri Mobile Entertainment Ltd. Developed by Kukouri Mobile Entertainment Ltd., and Plunge Interactive S.L. Licensed to and published by Wired Productions Ltd.   Kukouri, Tiny Troopers and the Tiny Troopers logo are trademarks of Kukouri Mobile Entertainment. All rights reserved.  PlayStation®3, PlayStation®4 and PlayStation®Vita is a registered trademark of Sony Interactive Entertainment Inc.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '英', 1426640400000, '射击', 6200, 5332, 0, 6200, 5332, 0, 0, 6200, 0, -1, 418, 335, 0, 1556006821458, 1558124401618, '');
