INSERT INTO `game` VALUES (1817, 1, 'JP0700-CUSA01057_00-ASIA000000000000', 'DRAGON BALL XENOVERSE', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/JP0700-CUSA01057_00-ASIA000000000000/1555046408000/image', '所有人类赛亚人化！亲手拯救DRAGON BALL的世界吧！<br>●主角是Son Goku和「你」！角色创造系统！<br>可选择各式各样种族与外貌创造自己进行养成。<br>从400种以上服装和200种以上的技能进行自定义，养出最强的战士吧！<br>当然，也可以使用动画角色！<br>●新的故事和新的敌人<br>DRAGON BALL的历史遭到恶化改窜。<br>与Son Goku和Vegeta等人一同奋战，夺回大家熟悉的DRAGON BALL世界！<br>●在「トキトキ都（惊奇都市）」获得全新在线体验<br>名为「トキトキ都（惊奇都市）」的城镇里可以接触到其他玩家！<br>体验对战与协力游玩等等各式各样的战斗吧！<br><br><b>Copyright：</b><br>©BIRD STUDIO/SHUEISHA, TOEI ANIMATION<br>©BIRD STUDIO/SHUEISHA ©2013 DRAGON BALL Z the Movie Production Committee<br>©2015 BANDAI NAMCO Games Inc.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '日', 1423098000000, '动作', 47900, 41194, 0, 47900, 41194, 0, 0, 47900, 0, -1, 462, 687, 0, 1556006874652, 1558124512882, '');
