INSERT INTO `game` VALUES (1992, 1, 'UP4424-CUSA13415_00-ASIA000000000000', 'Catch ＆ Release', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP4424-CUSA13415_00-ASIA000000000000/1557449018000/image', '<b><font color=\"orange\">游戏需要2个PS Move动态控制器才能播放。</font></b><br><br><font color=\"yellow\"><b>注意：本内容需要准备PlayStation®Move动态控制器。</b></font><br>乘上划艇，拿起船桨，准备投入一段放空身心、悠然自得的VR钓鱼之旅。<br> <br>《Catch & Release》带您驶进美丽的高山湖泊，在无拘无束之中探索大自然。在密林，山区，悬崖旁边，或在芦苇草盛开的岸边，随意选择垂钓位置，沉醉于钓鱼之乐。<br><br>您还可以使用各种各样的消閒道具, 如收听电台播放的音乐或打水漂等，感觉随意。<br><br>选择适当的诱饵，物色一处理想的钓场，挥动您的钓竿，志在钓得巨鱼！在鱼咬饵的一刻千万不可放松，您必须将猎物的力气耗尽，获胜于人和鱼的较技之中，享受钓上一刻的快感！<br><br>在游戏中玩家可以随意垂钓，享受玩乐，更可以接受挑战完成任务，从而获得更多的辅助道具及钓鱼装备，协助玩家钓上鱼中之王！<br><br><b>Copyright：</b><br>metricminds GmbH & Co. KG<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,日,英,韩', 1556240400000, '模拟', 17600, 15136, 0, 17600, 15136, 0, 0, 17600, 0, -1, 420, 5, 0, 1556254847670, 1558119805962, '');
INSERT INTO `game` VALUES (1993, 1, 'UP4572-CUSA12421_00-ASIA000000000012', 'HITMAN™ 2 - 迈阿密包', 'PS4', '游戏', '', 'https://store.playstation.com/store/api/chihiro/00_09_000/container/HK/zh/999/UP4572-CUSA12421_00-ASIA000000000012/1557478654000/image', '<b><font color=\"orange\">※另有包含此单独商品的套装包。请注意切勿重复购买。</font></b><br><br>“HITMAN™ 2 - 迈阿密包”将向你展示何为物超所值，并允许你访问新西兰及迈阿密地区全部的永久及未来内容。同时，你还可访问西梅斯坦狙击刺杀地图（Himmestein Sniper Assassin map），以及一套独家火烈鸟装扮！<br><br>还有，别错失了消灭限时特殊目标——由好莱坞影星肖恩·宾（Sean Bean）所扮演的马克·法帕（Mark Faba）的机会。该契约仅生效于5月3日-6月3日。<br><br><b>Copyright：</b><br>HITMAN™ © 2018 IO Interactive A/S. IO Interactive, IOI logo, HITMAN, HITMAN logo, and WORLD OF ASSASSINATION are trademarks or registered trademarks owned by or exclusively licensed to IO Interactive A/S. WBIE logo, WB SHIELD: ™ logo and Warner Bros. are trademarks or registered trademarks owned by Warner Bros. Home Entertainment Inc. All other trademarks are the property of their respective owners. Made in Denmark. All rights reserved.<br><br>购买或使用本产品需遵从SEN服务条款及使用协议。', '中,英', 1557190800000, '动作', 9300, 7998, 0, 9300, 7998, 0, 0, 9300, 0, -1, 500, 2, 0, 1557234046813, 1558119802948, 'UP4572-CUSA12421_00-ASIAPLACEHOLDER0');
